replacements, molecule = DATA.read.chomp.split("\n\n")
replacements = replacements.scan(/(\w+) \=> (\w+)/)
puts replacements.flat_map {|k,v|
  m = molecule.split(k, -1)
  (0...m.size-1).map {|i|
    m[0..i].join(k) + v + m[i+1..-1].join(k)
  }
}.uniq.size
